#!/usr/bin/python2.7
import os, shlex, subprocess, sys
def add_tm_support_to_path(): sys.path.append(os.environ['TM_BUNDLE_SUPPORT'])
add_tm_support_to_path()
from p4python.P4 import P4
# Make sure that a new TextMate window is spawned to input the commit description into os.environ['EDITOR'] = os.environ['TM_SUPPORT_PATH'] + '/bin/mate -w -l 25'
p4 = P4()
if not p4.connected(): p4.connect()
# Ensure human-readible output p4.tagged = False
print(p4.run('submit'))
